import React, {useCallback} from 'react';
import {StyleSheet, Text, TouchableOpacity, View} from 'react-native';
import {Ionicons} from '@expo/vector-icons';
import moment from 'moment';
import Store from '../store'

export default function Transaction({item, index})
{
    let date = moment(item.date);

    const onTransactionPress = useCallback(() =>
    {
        Store.dispatch({
            type: 'REMOVE_TRANSACTION',
            transaction: item
        });
    }, []);

    return (
        <TouchableOpacity
            style={[styles.container, {
                backgroundColor: index % 2
                    ? '#ffffff'
                    : '#f0f0f2'
            }]}
            onLongPress={() => onTransactionPress()}>
            <View style={styles.info}>
                <Text style={styles.infoLabel}>{item.name}</Text>
                <Text style={styles.infoValue}>{item.status} R$ {item.coast}</Text>
            </View>
            <View style={styles.header}>
                <Text style={styles.date}>{date.format("dddd")}, {date.fromNow()}</Text>
                {item.lock && <Ionicons name="md-lock-closed-outline"/>}
            </View>
        </TouchableOpacity>
    );
}

const styles = StyleSheet.create({
    container: {
        padding: 10,
        backgroundColor: 'white',
        marginBottom: 1
    },
    info: {
        flex: 1,
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center",
        marginHorizontal: 20,
    },
    header: {
        flex: 1,
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "space-between",
        marginHorizontal: 20,
        marginTop: 5
    },
    date: {
        color: '#a9a9a9',
        fontFamily: "Assistant-Light"
    },
    infoLabel: {
        flexShrink: 1,
        fontSize: 16,
        fontFamily: "Assistant-Bold"
    },
    infoValue: {
        flexShrink: 1,
        fontSize: 16,
        fontFamily: "Assistant"
    }
});
